import Vue from 'vue'
import VueRouter from 'vue-router'
import MainView from "@/views/MainView"
import AboutView from "@/views/AboutView"
import LoginView from "@/views/LoginView"
import RegisterView from "@/views/RegisterView";
import SettingView from "@/views/settingview/SettingView";
import UserView from "@/views/systemview/UserView";
import AdminView from "@/views/systemview/AdminView";
import RoleView from "@/views/systemview/RoleView";
import PictureView from "@/views/fileview/PictureView";

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: '首页',
    component: MainView,
    children: [
      {
        path: 'sys/user',
        name: '用户管理',
        component: UserView
      },
      {
        path: 'sys/admin',
        name: '管理员设置',
        component: AdminView
      },
      {
        path: 'sys/role',
        name: '权限设置',
        component: RoleView
      },
      {
        path: 'file/pic',
        name: '图片管理',
        component: PictureView
      },
      {
        path: 'setting',
        name: '设置',
        component: SettingView
      }
    ]
  },
  {
    path: '/about',
    name: '关于',
    component: AboutView
  },
  {
    path: '/login',
    name: '登录',
    component: LoginView
  },
  {
    path: '/register',
    name: '注册',
    component: RegisterView
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
