import JSEncrypt from "jsencrypt"

// http://web.chacuo.net/netrsakeypair 000216

const publicKey = "MFwwDQYJKoZIhvcNAQEBBQADSwAwSAJBALqt37mGMQtxKZ5Oq3h9hFRJIKDt3GpR\n"+
"XEyqC69kS3S86G7vragpP9EPE9zQrtb1nYIdqhOGxkL8u0+X77JFj4MCAwEAAQ=="

const privateKey = "MIIBVAIBADANBgkqhkiG9w0BAQEFAASCAT4wggE6AgEAAkEAuq3fuYYxC3Epnk6r\n"+
"eH2EVEkgoO3calFcTKoLr2RLdLzobu+tqCk/0Q8T3NCu1vWdgh2qE4bGQvy7T5fv\n"+
"skWPgwIDAQABAkB7GgerXttesTwBod0fMp4duq9ikBhGn+pev8++1EOR8ZRgfZ3R\n"+
"1ofKaEJQc5vYhj76mTwd06JE8QXdrhdiZsO5AiEA4QfWxblvltWt61bW+0sr58fx\n"+
"5K4LHfzwak7XOnEzjxUCIQDUXtuMLGA9XMltU94/6Sez1qmGVuICsycv3jx5qO8K\n"+
"NwIgPpI+bwZL+AX8cTbZWlChqbZYFX0rjDAb7YN0p0R40K0CIDO5bsuzOzV21fB0\n"+
"ztrOFMdubxezLZTuwYqdHCmdhar9AiEA017zHw8WqGaT18y9abA+1aCWrGpu24e/\n"+
"Kt3eEIDFNPU="

//加密
export function encrypt(txt) {
    const encryptor = new JSEncrypt()
    encryptor.setPublicKey(publicKey) //设置公钥
    return encryptor.encrypt(txt) //加密
}

//解密
export function decrypt(txt) {
    const encryptor = new JSEncrypt()
    encryptor.setPrivateKey(privateKey) //设置私钥
    return encryptor.decrypt(txt) //解密
}
