import Cookie from 'js-cookie';
import router from '@/router';
export default {
  name: "LoginView",
  data() {
    return {
      user: {},
      rules: {
        username: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }, {
          min: 4,
          max: 10,
          message: '长度在 4 到 10 个字符',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }, {
          min: 4,
          max: 100,
          message: '长度在 4 到 100 个字符',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    login() {
      this.$refs['userForm'].validate(async valid => {
        if (valid) {
          // this.axios.post('/user/login', this.user).then(res => {
          //   let result = res.data;
          //   if (result.code === 'C200') {
          //     localStorage.setItem('user', JSON.stringify(result.data));
          //     setToken(result.data.token);
          //     this.$message.success('登录成功');
          //     this.$router.push('/');
          //   } else {
          //     this.$message.error('登录失败');
          //   }
          // })
          //如果勾选了记住密码就要进行加密保存
          if (this.user.rememberMe) {
            Cookie.set("username", this.user.username, {
              expires: 30
            });
            Cookie.set("password", encrypt(this.user.password), {
              expires: 30
            });
            Cookie.set("rememberMe", this.user.rememberMe, {
              expires: 30
            });
          } else {
            Cookie.remove("username");
            Cookie.remove("password");
            Cookie.remove("rememberMe");
          }
          await this.axios.post('/login?username=' + this.user.username + '&password=' + this.user.password).then(res => {
            let result = res.data;
            console.log(result);
            if (result.code === 'C200') {
              setToken(result.data);
              this.$message.success(result.msg);
              router.replace('/');
            } else {
              this.$message.error(result.msg);
            }
          });
        } else {
          return false;
        }
      });
    }
  }
};
