import Vue from 'vue'
import VueRouter from 'vue-router'
import MainView from "@/views/MainView"
import AboutView from "@/views/AboutView"
import LoginView from "@/views/LoginView"
import RegisterView from "@/views/RegisterView";
import SettingView from "@/views/settingview/SettingView";
import UserView from "@/views/systemview/UserView";
import AdminView from "@/views/systemview/AdminView";
import RoleView from "@/views/systemview/RoleView";
import MenuView from '@/views/systemview/MenuView';
import PictureView from "@/views/fileview/PictureView";
import TextView from "@/views/fileview/TextView";
import GoodView from "@/views/shopview/GoodView";
import CategoryView from "@/views/shopview/CategoryView";
import OrderView from "@/views/shopview/OrderView";
import BannerView from "@/views/shopview/BannerView";
import NewsView from "@/views/shopview/NewsView";
import CartView from "@/views/shopview/CartView";
import HomeView from "@/views/chartview/HomeView";
import { getToken } from '@/utils/auth';
import store from '@/store';

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: '首页',
    component: MainView,
    children: [
      {
        path: 'home',
        name: '首页图表',
        component: HomeView
      },
      {
        path: 'sys/user',
        name: '系统管理',
        component: UserView
      },
      {
        path: 'sys/admin',
        name: '个人设置',
        component: AdminView
      },
      {
        path: 'sys/role',
        name: '角色管理',
        component: RoleView
      },
      {
        path: 'sys/menu',
        name: '菜单管理',
        component: MenuView
      },
      {
        path: 'file/pic',
        name: '图片管理',
        component: PictureView
      },
      {
        path: 'file/text',
        name: '文本管理',
        component: TextView
      },
      {
        path: 'shop/good',
        name: '商品管理',
        component: GoodView
      },
      {
        path: 'shop/category',
        name: '分类管理',
        component: CategoryView
      },
      {
        path: 'shop/order',
        name: '订单管理',
        component: OrderView
      },
      {
        path: 'shop/banner',
        name: '轮播图管理',
        component: BannerView
      },
      {
        path: 'shop/news',
        name: '新品管理',
        component: NewsView
      },
      {
        path: 'shop/cart',
        name: '购物车管理',
        component: CartView
      },
      {
        path: 'setting',
        name: '设置',
        component: SettingView
      }
    ]
  },
  {
    path: '/about',
    name: '关于',
    component: AboutView
  },
  {
    path: '/login',
    name: '登录',
    component: LoginView
  },
  {
    path: '/register',
    name: '注册',
    component: RegisterView
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

router.beforeEach((to, from, next) => {
  const whiteList = ['/login', '/register'];
  let token = getToken();
  let hasRoutes = store.state.hasRoutes;
  let menus = store.getters.GET_MENUS;
  if (token) {
    if (!hasRoutes) {
      bindRoute(menus);
      store.commit("set_routes_state", true);
    }
    next();
  } else {
    if (whiteList.includes(to.path)) {
      next();
    } else {
      next('/login');
    }
  }
})

//动态绑定路由
const bindRoute = (menus) => {
  let newRoutes = router.options.routes;
  menus.forEach(menu => {
    if (menu.children) {
      menu.children.forEach(m => {
        let route = menuToRoute(m, menu.menuName);
        if (route) {
          newRoutes[0].children.push(route)
        }
      })
    }
  })
}

//菜单转路由
const menuToRoute = (menu, parentName) => {

}

export default router
