import Vue from 'vue'
import Vuex from 'vuex'
import router from '@/router';
import { setToken, delToken, getToken } from '@/utils/auth';

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
  },
  getters: {
    get_token(state) {
      return getToken();
    },
    get_menus(state) {
      return JSON.parse(sessionStorage.getItem("menus"));
    }
  },
  mutations: {
    set_token(state, token) {
      setToken(token);
    },
    set_menus(state, menus) {
      sessionStorage.setItem("menus", JSON.stringify(menus));
    },
    logout() {
      sessionStorage.clear();
      delToken();
      router.replace('/blank?redirect=http://localhost:8080/login');
    }
  },
  actions: {
  },
  modules: {
  }
})
